window.addEventListener("DOMContentLoaded",(function(){burgerAnimation(),bg(),swiperTop()}));const burgerAnimation=()=>{const e=document.querySelector(".mobile-content__burger-menu"),t=document.querySelector(".header-footer"),o=document.querySelector("body");e.onclick=()=>{e.classList.toggle("active"),t.classList.toggle("active"),o.classList.toggle("body-lock")}},bg=()=>{document.querySelectorAll(".ibg").forEach(e=>{e.querySelector("img")&&(e.style.backgroundImage="url("+e.querySelector("img").getAttribute("src")+")",e.querySelector("img").style.display="none")})},swiperTop=()=>{function e(e){return e>9?e:"0"+e}new Swiper(".top-slider__container",{pagination:{el:".swiper-pagination",type:"fraction",formatFractionCurrent:e,formatFractionTotal:e},slidesPerView:1,slidesPerGroup:1,preloadImages:!1,lazy:{loadOnTransitionStart:!1,loadPrevNext:!0}})};